const numeros = [1, 2, 3, 4, 5];

console.log('Original:', numeros);
console.log('Dobro:', numeros.map(n => n * 2));
console.log('Pares:', numeros.filter(n => n % 2 === 0));
console.log('Soma:', numeros.reduce((a, b) => a + b, 0));

fetch-api.code:
async function buscarDados() {
    try {
        console.log('Buscando dados...');

        // Exemplo: buscar dados JSON
        const response = await fetch('https://jsonplaceholder.typicode.com/posts/1');
        const data = await response.json();

        console.log('Dados obtidos:', data);
        return data;

    } catch (error) {
        console.error('Erro ao buscar dados:', error);
        throw error;
    }
}

buscarDados();

timer-example.code:
// setTimeout - executa uma vez após delay
console.log('Agendando timer...');
setTimeout(() => {
    console.log('Timer executou após 2 segundos!');
}, 2000);

// setInterval - executa repetidamente
let contador = 0;
const intervalId = setInterval(() => {
    contador++;
    console.log(`Interval executou ${contador} vezes`);

    if (contador >= 5) {
        clearInterval(intervalId);
        console.log('Interval parado!');
    }
}, 1000);
